$(function() {

	$(".nav_toggle").click(function( click ) {
		$("header.global").toggleClass("nav--on");
		click.preventDefault();
	});

	$(".nav_close").click(function( click ) {
		$("header.global").removeClass("nav--on");
		click.preventDefault();
	});

	$(".text_view_trigger").click(function( click ) {
		$(".portfolio_body").addClass("text_view--on");
		click.preventDefault();
	});

	$(".image_view_trigger").click(function( click ) {
		$(".portfolio_body").removeClass("text_view--on");
		click.preventDefault();
	});

	$(".image_slider").flexslider({
		animation: "slide"
	});

	$("body").fitVids();


	$('.social_modals a').on('click', function(e){
		e.preventDefault();
		popup = window.open($(this).attr('href'), 'social','height=400,width=650');
		if (window.focus) {
			popup.focus();
		}
	});

});
